    item2 = types.KeyboardButton('üìñ–ü–æ–ª–∏—Å—Ç–∞—Ç—å –∫–∞—Ç–∞–ª–æ–≥ —Å —Ñ–∞—É–Ω–æ–π')
    item3 = types.KeyboardButton('üåç–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –•–∏–ª–ª–∏–∏')
    item4 = types.KeyboardButton('üíø–ì–¥–µ –Ω–∞–π—Ç–∏ –ú-–¥–∏—Å–∫–∏?')
    item5 = types.KeyboardButton('üìö–ö–∞—Ä–ª—Å–æ–Ω –∏ –ü–∏—Ç–µ—Ä—Å')
    item6 = types.KeyboardButton('üòé–°–µ–∫—Ä–µ—Ç–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è')

    markup.add(item1,item2,item3,item4,item5,item6)

    bot.send_message(message.chat.id,'<b>–ü—Ä–∏–≤–µ—Ç, —è –®–æ–Ω–∏ - —Ñ–æ—Ç–æ—Ä–µ–ø–æ—Ä—Ç–µ—Ä. –í—ã–±–∏—Ä–∞–π, —á—Ç–æ —Ç–µ–±–µ –Ω—É–∂–Ω–æ</b>',parse_mode = 'html',reply_markup = markup)



